import React, { useEffect } from 'react';
import particlesJS from 'particles.js';

const ParticlesBackground = () => {
  useEffect(() => {
    particlesJS('particles-js', {
      particles: {
        number: {
          value: 15,
          density: {
            enable: true,
            value_area: 300,
          },
        },
        color: {
          value: '',
        },
        shape: {
          type: 'triangle',
        },
        opacity: {
          value: 0.8,
          random: true,
          anim: {
            enable: true,
            speed: 1,
            opacity_min: 0.1,
            sync: false,
          },
        },
        size: {
          value: 5,
          random: true,
          anim: {
            enable: true,
            speed: 4,
            size_min: 0.3,
            sync: false,
          },
        },
        line_linked: {
          enable: true,
          distance: 150,
          color: '#ffffff',
          opacity: 0.4,
          width: 1,
        },
        move: {
          enable: true,
          speed: 2,
          direction: 'none',
          random: false,
          straight: false,
          out_mode: 'bounce',
          bounce: false,
        },
      },
      interactivity: {
        detect_on: 'canvas',
        events: {
          onhover: {
            enable: true,
            mode: 'repulse',
          },
          onclick: {
            enable: true,
            mode: 'push',
          },
          resize: true,
        },
      },
      retina_detect: true,
    });
  }, []);

  return (
    <div
      id="particles-js"
      style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
    ></div>
  );
};

export default ParticlesBackground;
